import { ref } from "vue";
import HomeHeader from "../components/HomeHeader.vue";

// 定义一个响应式变量，用于存储是否有 token

export default {
  __name: 'EmojiView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const hastoken = ref(false);

    // 判断 localStorage 中是否有 token
    hastoken.value = !!localStorage.getItem("token");
    const __returned__ = {
      hastoken,
      ref,
      HomeHeader
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
